function addTogether() {
  const [first, second] = arguments;

  // First argument is not a number
  if (typeof first !== "number") {
    return undefined;
  }

  // If second argument is not undefined and is not a number
  if (second !== undefined && typeof second !== "number") {
    return undefined;
  }

  // When second argument is defined, return the sum
  if (second !== undefined) {
    return first + second;
  } else {
    // When second argument is undefined, return a function
    return function(second) {
      // If the argument is not a number, return undefined
      if (typeof second !== "number") {
        return undefined;
      } else {
        // If the argument is a number, return the sum
        return first + second;
      }
    };
  }
}

addTogether(2,3);
