function translatePigLatin(str) {
  const vowels = ["a", "e", "i", "o", "u"];
  const firstLetter = str[0];
  if (vowels.includes(firstLetter)) {
    return str + "way";
  } else {
    for (let i = 1; i < str.length; i++) {
      if (vowels.includes(str[i])) {
        return str.slice(i) + str.slice(0, i) + "ay";
      }
    }
    return str + "ay";
  }
}

